SET SCHEMA "LEARNING_TO_NOTE";

DROP TABLE "PAIRS";
DROP TABLE "ENTITIES";
DROP TABLE "OFFSETS";
DROP TABLE "DOCUMENTS";


CREATE COLUMN TABLE "DOCUMENTS" (
                "ID" VARCHAR(255) PRIMARY KEY,
                "TEXT" NCLOB
              );

CREATE COLUMN TABLE "ENTITIES" (
                "ID" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                "DOC_ID" VARCHAR(255),
                "TYPE" VARCHAR(255)
              );

CREATE COLUMN TABLE "OFFSETS" (
                "START" INT,
                "END" INT,
                "ENTITY_ID" INT
              );

CREATE COLUMN TABLE "PAIRS" (
                "ID" INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
                "E1_ID" VARCHAR(255),
                "E2_ID" VARCHAR(255),
                "DDI" TINYINT,
                "TYPE" VARCHAR(255)
              );


TRUNCATE TABLE "PAIRS";
TRUNCATE TABLE "OFFSETS";
TRUNCATE TABLE "ENTITIES";
TRUNCATE TABLE "DOCUMENTS";
